import React from 'react';
import { useHistory } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';

const Detail = (props) => {
    console.log(props);
    const history = useHistory();
    const daydays = props.dayday;


    // const days = useParams();
    // console.log(days);

    const [count, setCount] = React.useState(5);
    const circle_count = Array.from({ length: count }, (el, i) => i);
    console.log(circle_count);

    return (
        <div>
            <div>
                <h2>
                    How was your {daydays[0]}
                </h2>
                <Circlewrap>
                    {circle_count.map((num, index) => {
                        return (
                            <Nonclick key={index}>
                            </Nonclick>
                        )
                    })}
                </Circlewrap>
                <Complete
                    onClick={() => { history.push("/") }}>
                    Complete
                </Complete>
            </div>
        </div>
    )
}

const Circlewrap = styled.div`
    width: 300px;
    display: flex;
    margin: auto;
`;

const Nonclick = styled.div`
    display: flex;
    width: 30px;
    height: 30px;
    margin: 22px 10px;
    border: 5px solid lightgrey;
    border-radius: 50%;
    background-color: white;
`;

const Complete = styled.button`
    outline: 0;
    border: 1px solid transparent;
    border-radius: 20px;
    background-color: seagreen;
    color: whitesmoke;

    width: 120px;
    height: 40px;
    margin: 20px auto;

    font-size: 14px;
    cursor: pointer;
`;

// url 파라미터를 dayday의 요소로 가져와서
// if url 파라미터와 dayday[i]가 일치하면 dayday[i] 출력


export default Detail;
