import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import Circle from './Circle';


const Myweek = (props) => {

    const history = useHistory();
    const daydays = props.dayday;
    console.log(daydays);

    return (
        <div>
            <Title>How you  <Highlight>feel Today</Highlight>?</Title>

            {daydays.map((day, index) => {
                console.log(day);
                return (
                    <div key={index} style={{ display: "flex", }}>
                        <Day>{day}</Day>
                        <Circle />
                        <Link to={`/detail/${day}`}>
                            <Detail >
                            </Detail>
                        </Link>
                    </div>
                );
            })}
        </div>
    );
};

const Highlight = styled.span`
    background-image: linear-gradient(transparent 60%, #F8CD07 40%);
`;

const Container = styled.div`
    display: flex;
`;

const Title = styled.h2`
    margin: 30px auto;
`;

const Day = styled.div`
    width: 70px;
    font: 14px bold;
    vertical-align: middle;
    line-height: 65px;
    text-align: right;
    margin-right: 10px;
`;

const Detail = styled.div`
    width: 12px;
    height: 12px;
    margin-top: 22px;


    border-top: 3px solid grey;
    border-right: 3px solid grey;
    transform: rotate(45deg);

    cursor: pointer;
    vertical-align: middle;
    line-height: 65px;

    &:hover {
        border-top: 3px solid #F8CD07;
        border-right: 3px solid #F8CD07;
    }
`;

// border-top: 16px solid transparent;
//     border-bottom: 16px solid transparent;
//     borderright: 16px solid none;
//     border-left: 24px solid seagreen;

export default Myweek;
