import React from 'react';

const Circle = (props) => {

    const [count, setCount] = React.useState(5);

    const circle_count = Array.from({ length: count }, (el, i) => i);
    console.log(circle_count);

    return (
        <div style={{
            width: "320px",
            display: "flex",
            margin: "auto",
        }}>
            {circle_count.map((num, index) => {
                if (index <= Math.floor(Math.random() * 5)) {
                    return (
                        <div key={index}
                            style={{
                                width: "30px",
                                height: "30px",
                                margin: "12px 10px",
                                border: "5px solid lightgrey",
                                borderRadius: "50%",
                                backgroundColor: "white",
                            }}>
                        </div>
                    )
                }
            })}
        </div>
    )
}


// 0~4까지 랜덤 정수 Math.floor(Math.random()*5)
// if i = 0; i < index; i++ 
// if < 랜덤 정수 (랜덤 정수 전까지는 채우기, 나머지는 비우기)


export default Circle;
