import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import NotFound from './NotFound';

const Detail = (props) => {

    const history = useHistory();
    const daydays = props.dayday;
    let parameter = props.match.params.day;
    console.log(parameter);

    const [count, setCount] = React.useState(5);
    const circle_count = Array.from({ length: count }, (el, i) => i);
    console.log(circle_count);

    const [clicked, setClicked] = useState([false, false, false, false, false]);

    const handleStarClick = (e, index) => {
        e.preventDefault();
        let clickStates = [...clicked];
        for (let i = 0; i < 5; i++) {
            if (i <= index) clickStates[i] = true;
            else clickStates[i] = false;
        }
        setClicked(clickStates);
    };


    if (parameter) {
        return (
            <div>
                <h2>How was your <Parameter>{parameter}</Parameter>?</h2>
                <Circlewrap>
                    <div onClick={(e) => handleStarClick(e, 0)} style={clicked[0] ? {borderColor: "seagreen"} : null}/>
                    <div onClick={(e) => handleStarClick(e, 1)} style={clicked[1] ? {borderColor: "seagreen"} : null}/>
                    <div onClick={(e) => handleStarClick(e, 2)} style={clicked[2] ? {borderColor: "seagreen"} : null}/>
                    <div onClick={(e) => handleStarClick(e, 3)} style={clicked[3] ? {borderColor: "seagreen"} : null}/>
                    <div onClick={(e) => handleStarClick(e, 4)} style={clicked[4] ? {borderColor: "seagreen"} : null}/>
                </Circlewrap>
                <Complete
                    onClick={() => { history.push("/") }}>
                    Complete
                </Complete>
            </div >
        )
    } else {
        return <NotFound />;
    }
}


const Parameter = styled.span`
    background-image: linear-gradient(transparent 60%, #F8CD07 40%);
`;

const Circlewrap = styled.div`
    width: 275px;
    display: flex;
    margin: auto;

    div {
        display: flex;
        width: 25px;
        height: 25px;
        margin: 22px 10px;
        border: 5px solid lightgrey;
        border-radius: 50%;
        background-color: white;
    }

    div:hover {
        border-color: seagreen;
    }
`;

// const Nonclick = styled.div`
//     display: flex;
//     width: 25px;
//     height: 25px;
//     margin: 22px 10px;
//     border: 5px solid lightgrey;
//     border-radius: 50%;
//     background-color: white;
// `;

const Complete = styled.button`
    outline: 0;
    border: 1px solid transparent;
    border-radius: 20px;
    background-color: seagreen;
    color: whitesmoke;

    width: 120px;
    height: 40px;
    margin: 20px auto;

    font-size: 14px;
    cursor: pointer;
`;

// url 파라미터를 dayday의 요소로 가져와서
// if url 파라미터와 dayday[i]가 일치하면 dayday[i] 출력


export default Detail;
